import React from 'react'
import GameCard from './shared/GameCard'
import axios from 'axios'




class Home extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            games: []
        }
    }


    componentDidMount() {

        this.getGames();

    }



    // getGames = async () => {
    //     console.log('This works right?')
    //     let response = await axios.get(`https://localhost:3000/games`)
    //     console.log(response.data)
    //     this.setState({
    //         games: response.data
    //     })
    // }

    getGames = async() => {
        const response = await axios.get('/games');
        const data = response.data;
        this.setState({
            games: data
        })
    }




    render() {
        return (
            <div>
            {this.state.games.map(game => {
                return (
                    <div key={game.id}>
                        <GameCard title={game.title}/>
                    </div>
                )
            })}
            </div>
        )
    }
}


export default Home
